import "date"
from(bucket: "multisma2/autogen")
  |> range(start: -32d)
  |> filter(fn: (r) => r._measurement == "production" and r.inverter== "site")
  |> filter(fn: (r) => r._field == "total" or (r._field == "today" and (date.monthDay(t: r._time) == 1)))
  |> last()
  |> pivot(rowKey:["_measurement"], columnKey: ["_field"], valueColumn: "_value")
  |> map(fn: (r) => ({
      r with _value: float(v: (r.total - r.today)) / 1000.0
    })
  )
  |> drop(columns: ["today", "total"])

import "date"
