  						(r._field == "today" and (date.monthDay(t: r._time) == 1) and (date.month(t: r._time) == 1))
      		)
  |> last()
  |> pivot(rowKey:["_measurement"], columnKey: ["_field"], valueColumn: "_value")
  |> map(fn: (r) => ({
