from(bucket: "multisma2/autogen")
  |> range(start: -1d)
  |> filter(fn: (r) => r._measurement == "production" and r.inverter== "site" and r._field == "total")
  |> last()
  |> map(fn: (r) => ({
      r with _value: float(v: r._value) / 1000000.0
    })
  )

from(bucket: "multisma2/autogen")
  |> range(start: -5d)
  |> filter(fn: (r) => r._measurement == "production" and r._field == "total" and r.inverter == "site")
  |> elapsed(unit: 1s)
  |> difference(nonNegative: false, columns: ["_value"])
  |> map(fn: (r) => ({
				r with _value: float(v: r._value) * 3600.0 / float(v: r.elapsed)
            }))
  |> drop(columns: ["elapsed"])
  |> yield(name: "production")

from(bucket: "multisma2/autogen")
  |> range(start: -5d)
  |> filter(fn: (r) => r._measurement == "production" and (r._field == "irradiance"))

from(bucket: "multisma2/autogen")
  |> range(start: -1d)
  |> filter(fn: (r) => (r._measurement == "production") and
  						(r.inverter == "site") and
                        ((r._field == "total") or (r._field == "today"))
     		)
  |> last()
  |> map(fn: (r) => ({
      _time: r._time,
      _value: float(v: r._value) / -1000.0,
    }))
  |> sort(columns: ["_time"], desc: true)
  |> difference(nonNegative: false, columns: ["_value"])
  |> map(fn: (r) => ({
      r with _value: float(v: r._value) * 0.18991
    })
  )
  |> yield(name: "today")

